myApp.controller("themInfoUserController",function(n,t,i,r,u,f){n.User={};n.ChucVu=angular.element("#ChucVu").val();n.chooseImage=function(){var t=new CKFinder;t.selectActionFunction=function(t){n.User.image=t;n.$apply()};t.SelectFunction="ShowFileInfo";t.popup()};n.currentIdUser=f.getParameterByName("UserId");n.currentIdUser&&t.get("/API/UserAPI/"+n.currentIdUser).success(function(t){n.User={UserId:t.UserId,Username:t.Username,hoten:t.hoten,sdt:t.sdt,image:t.image,chuvu:t.chuvu}});n.saveUser=function(){n.User.chuvu=n.ChucVu;t.put("/API/UserAPI/"+n.User.UserId,n.User).success(function(){toastr.success("Thành công","Lưu thông tin cá nhân");i.location.href="/Admin"}).error(function(){toastr.error("Thất bại","Thêm thông tin cá nhân")})};n.cancel=function(){i.location.href="/Admin"}});