frontApp.controller("showProductController",["$scope","$http","$window","$sce",function(n,t,i,r){n.product={};n.relatedProducts=[];n.idCategoryProduct="";n.idProduct=angular.element("#idProduct").val();t.get("/API/ProductsAPI/"+n.idProduct).success(function(i){n.product=i;n.product.content=r.trustAsHtml(i.content);n.product.timeStart=n.product.timeStart!=null?new Date(n.product.timeStart):null;n.idCategoryProduct=i.idCategoryProduct;t.get("/API/ProductsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategoryProduct==n.idCategoryProduct&&t.idProduct!=n.idProduct&&n.relatedProducts.push(t)})})})}]);